import React, { useState, useEffect } from "react";
import Meta from "../components/Meta";
import {
  Row,
  Col,
  Image,
  ListGroup,
  Card,
  Button,
  Form,
} from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import Message from "../components/Message";
import Loader from "../components/Loader";
import Carousel from "react-bootstrap/Carousel";
import {
  listProductDetails,
  createProductReview,
} from "../actions/productActions";
import { sendEmail } from "../actions/userActions";
import { PRODUCT_REVIEW_RESET } from "../types/productConstants";

const ProductScreen = ({ match, history }) => {
  const [text, setText] = useState("");
  const [comment, setComment] = useState("");
  const [sendMail, setSendMail] = useState(false);
  const [emailSent, setEmailSent] = useState(false);

  const dispatch = useDispatch();
  const emailReducer = useSelector((state) => state.emailReducer);
  const {
    loading: loadingEmail,
    error: errorEmail,
    data: dataEmail,
  } = emailReducer;

  const productReviewCreate = useSelector((state) => state.productReviewCreate);
  const {
    loading: loadingReview,
    error: errorReview,
    success: successReview,
  } = productReviewCreate;

  const userLogin = useSelector((state) => state.userLogin);
  const { userData } = userLogin;

  const productDetails = useSelector((state) => state.productDetails);
  const { loading, error, product } = productDetails;

  useEffect(() => {
    if (successReview) {
      setComment("");
      dispatch({ type: PRODUCT_REVIEW_RESET });
    }
    dispatch(listProductDetails(match.params.id));
  }, [match.params.id, dispatch, successReview]);

  // ✅ Email Form Submit Handler
  const emailSubmit = (e) => {
    e.preventDefault();

    if (!text.trim()) {
      return;
    }

    setEmailSent(true);

    dispatch(
      sendEmail(
        product?.seller?.selleremail,
        text,
        userData?.name,
        userData?.address,
        product?.name,
        userData?.email,
        userData?.contact?.phone_no
      )
    );

    setText("");
    setSendMail(false);
    setTimeout(() => {
      setEmailSent(false);
    }, 10000);
  };


  const sendEMAIL = () => {

    setSendMail(true);
  };

  // ✅ Cancel Email Form
  const cancelHandler = () => {
    setSendMail(false);
  };

  return (
    <>
      <Link to="/" className="btn btn-success my-3">
        Go Back
      </Link>
      <br />
      {userData && userData._id === product.user && (
        <Link
          to={`/admin/product/${match.params.id}/edit`}
          className="btn btn-primary my-3"
        >
          Edit Product
        </Link>
      )}

      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <>
          <Meta title={product.name} />
          <Row className="mb-2">
            <Col md={6} className="image-area">
              {product.images && product.images.length > 0 ? (
                <Carousel>
                  {product.images.map((image, index) => (
                    <Carousel.Item key={index}>
                      <Image
                        className="d-block w-100"
                        src={image.image1 || image}
                        alt={`Slide ${index + 1}`}
                      />
                    </Carousel.Item>
                  ))}
                </Carousel>
              ) : (
                <Message variant="info">No images available</Message>
              )}
            </Col>

            <Col className="borderaround setheight" md={6}>
              <p className="details">
                <i className="fas fa-info"></i> General Details
              </p>
              <Row>
                <Col className="product" md={4}>
                  <ul>
                    <li> Product Id:</li>
                    <li> Posted On:</li>
                    <li> Expires On:</li>
                    <li> Product:</li>
                  </ul>
                </Col>
                <Col md={8}>
                  <ul>
                    <li>{product._id}</li>
                    <li>{product?.createdAt?.substring(0, 10)}</li>
                    <li>{product?.expiresOn?.substring(0, 10)}</li>
                    <li>{product.name}</li>
                  </ul>
                </Col>
              </Row>
            </Col>
          </Row>

          {loadingEmail && <Loader />}
          {errorEmail && <Message variant="danger">{errorEmail}</Message>}
          {dataEmail && emailSent && (
            <Message variant="success">{dataEmail.response}</Message>
          )}

          <Row>
            <Col className="borderaround mt-5" md={10}>
              <p className="details">
                <i className="fas fa-info"></i> Seller Details
              </p>
              <Row>
                <Col className="product" md={4}>
                  <ul>
                    <li>Name:</li>
                    <li>Email:</li>
                    <li>Address:</li>
                    <li>Phone:</li>
                  </ul>
                </Col>
                <Col md={8}>
                  <ul>
                    <li>{product?.seller?.sellername}</li>
                    <li>
                      <button
                        className="emailbutton btn-success"
                        onClick={sendEMAIL}
                      >
                        Send Email
                      </button>
                    </li>
                    <li>{product?.seller?.selleraddress}</li>
                    <li>{product?.seller?.phoneNo?.mobile}</li>
                  </ul>
                </Col>
              </Row>

              {sendMail && (
                <Form onSubmit={emailSubmit}>
                  <Form.Group controlId="emailText">
                    <Form.Label>Enter your message:</Form.Label>
                    <Form.Control
                      as="textarea"
                      rows={3}
                      value={text}
                      onChange={(e) => setText(e.target.value)}
                    />
                  </Form.Group>
                  <Button type="submit" variant="primary">
                    Send
                  </Button>
                  <Button variant="danger" onClick={cancelHandler} className="ml-2">
                    Cancel
                  </Button>
                </Form>
              )}
            </Col>
          </Row>
        </>
      )}
    </>
  );
};

export default ProductScreen;
