import React, { useState, useEffect } from 'react';
import Meta from '../components/Meta';
import { Row, Col, Image, ListGroup, Card, Button, Form, Modal } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import Message from '../components/Message';
import Loader from '../components/Loader';
import Carousel from 'react-bootstrap/Carousel';
import { listProductDetails, createProductReview } from '../actions/productActions';
import { sendEmail } from '../actions/userActions';
import { PRODUCT_REVIEW_RESET } from '../types/productConstants';

const ProductScreen = ({ match, history }) => {
  const [text, setText] = useState('');
  const [comment, setComment] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);

  const dispatch = useDispatch();

  const productDetails = useSelector((state) => state.productDetails);
  const { loading, error, product } = productDetails;

  useEffect(() => {
    dispatch(listProductDetails(match.params.id));
  }, [match.params.id, dispatch]);

  const openModal = (image) => {
    setSelectedImage(image);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedImage(null);
  };

  return (
    <>
      <Link to='/' className='btn btn-success my-3'>Go Back</Link>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant='danger'>{error}</Message>
      ) : (
        <>
          <Meta title={product.name} />
          <Row className='row mb-2'>
            <Col md={6} className='image-area'>
              <Carousel>
                {product.images.map((image) => (
                  <Carousel.Item key={image._id}>
                    <Image
                      className='d-block w-100'
                      src={image?.image1}
                      alt='Product slide'
                      onClick={() => openModal(image?.image1)}
                      style={{ cursor: 'pointer' }}
                    />
                  </Carousel.Item>
                ))}
              </Carousel>
            </Col>
          </Row>

          <Modal show={showModal} onHide={closeModal} centered>
            <Modal.Body>
              <Image src={selectedImage} className='img-fluid' />
            </Modal.Body>
          </Modal>
        </>
      )}
    </>
  );
};

export default ProductScreen;
